    <div class="absolute top-[95%] left-[180px] right-[180px] bg-[#3C172E] h-[360px] rounded-[64px] z-0 drop-shadow-3xl"> 
        <div class="mx-24">

            <div id="declarationSection">
                <div class="text-white">
                    <div class="font-lora text-xl font-semibold mt-24">Declaration Status</div>
                    <div class="font-inter mt-2 text-sm">Check import and export declaration status</div>
                </div>

                <div class="flex mt-8 space-x-8 items-end">
                    <div>
                        <div class="font-inter text-red-300 font-semibold text-xs mb-1">C Number</div>
                        <input type="text" placeholder="C1234" class="rounded-lg w-[320px]" style="font-size: 0.75rem;">
                    </div>

                    <div>
                        <div class="font-inter text-red-300 font-semibold text-xs mb-1">R Number</div>
                        <input type="text" placeholder="R/1234/00MP" class="rounded-lg w-[320px]" style="font-size: 0.75rem;">
                    </div>

                    <div>
                        <x-primary-button onclick="showToast('No search here. Youâ€™ll have to look elsewhere!'); event.preventDefault();">
                            Search
                        </x-primary-button>
                    </div>
                </div>                
            </div>

            <div id="trackingSection" class="hidden">
                <div class="text-white">
                    <div class="font-lora text-xl font-semibold mt-24">Container Tracking</div>
                    <div class="font-inter mt-2 text-sm">Retrieve Container status and ETA</div>
                </div>

                <div class="flex mt-8 space-x-8 items-end">
                    <div>
                        <div class="font-inter text-red-300 font-semibold text-xs mb-1">Container ID</div>
                        <input type="text" placeholder="Container ID" class="rounded-lg w-[672px]" style="font-size: 0.75rem;">
                    </div>
                    <div>
                        <x-primary-button onclick="showToast('Search? Not today, my friend!'); event.preventDefault();">
                            Search
                        </x-primary-button>
                    </div>
                </div>
            </div>
        </div>                
    </div>
</div>

<div id="toastMessage" class="fixed top-5 left-1/2 transform -translate-x-1/2 bg-gradient-to-br from-[#F6D9D8] from-20% to-[#E9E7F1] border-2 border-[#3C172E] border-opacity-20 text-black font-inter font-semibold p-4 rounded-xl drop-shadow-lg transition-all duration-300 opacity-0 pointer-events-none z-50">
    <p id="toastText"></p>
</div>

<script>
    function showLookUpSection(section) {
        document.getElementById('declarationSection').classList.remove('hidden');
        document.getElementById('trackingSection').classList.add('hidden');
        
        if (section === 'declaration' ) {
            document.getElementById('declarationSection').classList.remove('hidden', 'transition', 'ease-in-out', 'duration-300');
            document.getElementById('trackingSection').classList.add('hidden', 'transition', 'ease-in-out', 'duration-300');

            document.getElementById('declarationDiv').classList.add('bg-[#3C172E]', 'text-white', 'transition', 'ease-in-out', 'duration-300');
            document.getElementById('trackingDiv').classList.remove('bg-[#3C172E]', 'text-white', 'transition', 'ease-in-out', 'duration-300');
        }
        else if (section === 'tracking') {
            document.getElementById('declarationSection').classList.add('hidden', 'transition', 'ease-in-out', 'duration-300');
            document.getElementById('trackingSection').classList.remove('hidden', 'transition', 'ease-in-out', 'duration-300');

            document.getElementById('declarationDiv').classList.remove('bg-[#3C172E]', 'text-white', 'transition', 'ease-in-out', 'duration-300');
            document.getElementById('trackingDiv').classList.add('bg-[#3C172E]', 'text-white', 'transition', 'ease-in-out', 'duration-300');
        }
    }

    window.addEventListener('load', function() {
        showLookUpSection('declaration');
    });

    function showToast(message) {
        const toast = document.getElementById('toastMessage');
        const toastText = document.getElementById('toastText');
        
        toastText.textContent = message;
        
        toast.style.visibility = 'visible';
        toast.style.opacity = '1';  
        
        setTimeout(function() {
            toast.style.opacity = '0'; 
            setTimeout(function() {
                toast.style.visibility = 'hidden';  
            }, 300); 
        }, 3000);  
    }
</script>




       