                <div class="flex flex-col justify-center cursor-pointer" onclick="showToast('These procedures do not lead anywhere...'); event.preventDefault();">
                    <h2 class="font-inter text-xl font-bold mx-2 text-black mt-16">{{ $procedure->heading }}</h2>
                    <p class="text-sm text-gray-500 mx-2">{{ ucfirst($procedure->type) }}</p>
                    <p class="font-inter text-md text-gray-700 mt-2 mx-2">{{ Str::limit($procedure->details, 100) }}</p>
