    </div>
</div>

<script>
    function bgChangeProcedureSection(section) {   
        const importBtn = document.getElementById('importDiv');
        const exportBtn = document.getElementById('exportDiv');

        importBtn.classList.remove('bg-red-100', 'border-2', 'border-red-200');
        exportBtn.classList.remove('bg-red-100', 'border-2', 'border-red-200');
             
        if (section === 'import' ) {
            document.getElementById('importDiv').classList.add('bg-red-100', 'border-2', 'border-red-200', 'transition', 'ease-in-out', 'duration-300');
            document.getElementById('exportDiv').classList.remove('bg-red-100', 'border-2', 'border-red-200' , 'transition', 'ease-in-out', 'duration-300');
        }
        else if (section === 'export') {
            document.getElementById('importDiv').classList.remove('bg-red-100', 'border-2', 'border-red-200' , 'transition', 'ease-in-out', 'duration-300');
            document.getElementById('exportDiv').classList.add('bg-red-100', 'border-2', 'border-red-200' , 'transition', 'ease-in-out', 'duration-300');
        }
    }

    window.addEventListener('load', function() {
        bgChangeProcedureSection('import');
    });

    function showToast(message) {
        const toast = document.getElementById('toastMessage');
        const toastText = document.getElementById('toastText');
        
        toastText.textContent = message;
        
        toast.style.visibility = 'visible';
        toast.style.opacity = '1';  
        
        setTimeout(function() {
            toast.style.opacity = '0'; 
            setTimeout(function() {
                toast.style.visibility = 'hidden';  
            }, 300); 
        }, 3000);  
    }
</script>
